import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header'; 
import Footer from '../components/footer';

const NewOrderForm = () => {
  const [formData, setFormData] = useState({

    firstName: '',
    lastName: '',
    items: '',
    itemType: 'regular load',
    kilos: '',
    description: ''
  });

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      let customer;

      if (formData.customerOption === 'existing' && selectedCustomer) {
        customer = selectedCustomer;
      } else {
        const newCustomerData = {
          firstName: formData.firstName,
          lastName: formData.lastName
          // Add other customer data as needed
        };
        const response = await axios.post('/customer', newCustomerData);
        customer = response.data.customer;
      }

      const orderData = {
        customer: customer._id,
        items: [
          {
            type: formData.itemType,
            kilo: formData.kilos
          }
        ],
        description: formData.description
      };

      await axios.post('http://localhost:3000/order/postrder', orderData);
      alert('Order submitted successfully');
    } catch (error) {
      console.error('Error submitting order:', error);
      alert('Error submitting order');
    }
  };

  return (
    <div>
      <Header />
      <div className="container">
        <br />
        <h1 className="mb-4">New Order Form</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="firstName">First Name:</label>
            <input type="text" id="firstName" value={firstName} onChange={(event) => setFirstName(event.target.value)} />
          </div>

          <div className="form-group">
          <label htmlFor="lastName">Last Name: </label>
          <input type="text" id="lastName" value={lastName} onChange={(event) => setLastName(event.target.value)} />
        </div>

        <div className="form-group">
            <label htmlFor="itemType">Item Type</label>
            <select className="form-control" id="itemType" name="itemType" value={formData.itemType} onChange={handleInputChange}>
              <option value="regular load">Regular Load</option>
              <option value="heavy load">Heavy Load</option>
            </select>
        </div>

         <div className="form-group">
            <label htmlFor="kilos">Kilos</label>
            <input type="number" className="form-control" id="kilos" name="kilos" value={formData.kilos} onChange={handleInputChange} />
        </div>

        <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea className="form-control" id="description" name="description" value={formData.description} onChange={handleInputChange}></textarea>
        </div>

            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
      </div>
      <Footer/>
    </div>
  );
};

export default NewOrderForm;
