import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header'; 
import Footer from '../components/footer';

const OrderList = () => {
  // State to store orders
  const [orders, setOrders] = useState([]);

  // Function to fetch orders from the server
  const fetchOrders = async () => {
    try {
      const response = await axios.get('/order/getorders'); // Assuming your backend endpoint for fetching orders is '/api/orders'
      setOrders(response.data.orders); // Assuming your API response contains an array of orders
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  // Fetch orders on component mount
  useEffect(() => {
    fetchOrders();
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  return (
    <div >
        <Header />{}
        <div className="container">
        <br /><br />
        <h1>Order List</h1>
        <a href="/labaduh/order/new" className="btn btn-success">Create New Order</a>
        <div className="row mt-4">
            {orders.map(order => (
            <div key={order._id} className="col-md-4 mb-4">
                <div className="card">
                <div className="card-body">
                    <h5 className="card-title">{order.name}</h5>
                    <p className="card-text">{new Date(order.createdAt).toLocaleDateString()}</p>
                    {/* Render other order details as needed */}
                </div>
                </div>
            </div>
            ))}
        </div>
        </div>
        <Footer/>{}
    </div>
  );
};

export default OrderList;
