import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header'; 
import Footer from '../components/footer';

const NewOrderForm = () => {
  const [formData, setFormData] = useState({
    customerOption: 'existing', // Default to selecting from existing customers
    customerName: '',
    items: '',
    itemType: 'regular load',
    kilos: '',
    description: ''
  });

  const [registeredCustomers, setRegisteredCustomers] = useState([]);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get('/api/customer');
      setRegisteredCustomers(response.data.customers.filter(customer => customer.registered)); // Only set registered customers
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      let customer;

      if (formData.customerOption === 'existing') {
        // No need to search customers if selecting from existing ones
        customer = registeredCustomers.find(cust => cust.firstName.toLowerCase() === formData.customerName.toLowerCase());
      } else {
        const newCustomerData = {
          firstName: formData.customerName
          // Add other customer data as needed
        };
        const response = await axios.post('/customer', newCustomerData);
        customer = response.data.customer;
      }

      const orderData = {
        customer: customer._id,
        items: [
          {
            type: formData.itemType,
            kilo: formData.kilos
          }
        ],
        description: formData.description
      };

      await axios.post('/order/new', orderData);
      alert('Order submitted successfully');
    } catch (error) {
      console.error('Error submitting order:', error);
      alert('Error submitting order');
    }
  };

  return (
    <div>
      <Header />
      <div className="container">
        <br />
        <h1 className="mb-4">New Order Form</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="customerOption">Select Customer</label>
            <select className="form-control" id="customerOption" name="customerOption" value={formData.customerOption} onChange={handleInputChange}>
              <option value="existing">Existing Customer</option>
              <option value="new">New Customer</option>
            </select>
          </div>
          {formData.customerOption === 'existing' ? (
            <div className="form-group">
              <label htmlFor="customerName">Customer Name</label>
              <select className="form-control" id="customerName" name="customerName" value={formData.customerName} onChange={handleInputChange}>
                <option value="">Select Customer</option>
                {registeredCustomers.map(customer => (
                  <option key={customer._id} value={customer.firstName}>{customer.firstName}</option>
                ))}
              </select>
            </div>
          ) : (
            <div className="form-group">
              <label htmlFor="customerName">Customer Name</label>
              <input type="text" className="form-control" id="customerName" name="customerName" value={formData.customerName} onChange={handleInputChange} />
            </div>
          )}
          <div className="form-group">
            <label htmlFor="itemType">Item Type</label>
            <select className="form-control" id="itemType" name="itemType" value={formData.itemType} onChange={handleInputChange}>
              <option value="regular load">Regular Load</option>
              <option value="heavy load">Heavy Load</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="kilos">Kilos</label>
            <input type="number" className="form-control" id="kilos" name="kilos" value={formData.kilos} onChange={handleInputChange} />
          </div>
          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea className="form-control" id="description" name="description" value={formData.description} onChange={handleInputChange}></textarea>
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
        </form>
      </div>
      <Footer/>
    </div>
  );
};

export default NewOrderForm;
