  import React, { useState } from 'react';
  import axios from 'axios';
  import Header1 from '../components/header1'; // Import Header component
  import Footer from '../components/footer'; // Import Footer component
  import '../css/landing.css';

  const Home = () => {
    const [orderNumber, setOrderNumber] = useState('');
  const [order, setOrder] = useState(null);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/order/search/${orderNumber}`);
      setOrder(response.data);
      setError('');
    } catch (error) {
      setError('Order not found');
      setOrder(null);
    }
  };

    return (
      <div>
        <Header1 /> {/* Include Header component */}
        
        <div className="search-container">
          <input 
            type="text" 
            value={orderNumber} 
            onChange={(e) => setOrderNumber(e.target.value)} 
            placeholder="Enter Order Number..." 
          />
          <button onClick={handleSearch}>Check Status</button>
        </div>
        
        <div className="results-container">
          {orderStatus ? (
            <div>
              <h3>Order Status</h3>
              <p>Order Number: {orderStatus.orderNumber}</p>
              <p>Status: {orderStatus.status}</p>
            </div>
          ) : (
            <p>Enter an order number to check the status.</p>
          )}
        </div>

        <Footer /> {/* Include Footer component */}
      </div>
    );
  };

  export default Home;
