import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';  // Import Chart.js
import Header from '../components/header';
import Footer from '../components/footer';

const BpmList = () => {
  const [bpm, setBPM] = useState([]);
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [
      {
        label: 'Average Beat',
        data: [],
        borderColor: 'rgba(75,192,192,1)',
        backgroundColor: 'rgba(75,192,192,0.2)',
        fill: true,
      },
    ],
  });

  useEffect(() => {
    const fetchBpm = async () => {
      try {
        const response = await axios.get('http://localhost:3000/bpm/getlist');
        setBPM(response.data.bpm);

        const labels = response.data.bpm.map(bpm => new Date(bpm.timestamp).toLocaleString());
        const data = response.data.bpm.map(bpm => bpm.beatAvg);

        setChartData({
          labels,
          datasets: [
            {
              label: 'Average Beat',
              data,
              borderColor: 'rgba(75,192,192,1)',
              backgroundColor: 'rgba(75,192,192,0.2)',
              fill: true,
            },
          ],
        });
      } catch (error) {
        console.error('Error fetching Bpm List:', error);
      }
    };

    fetchBpm();

    const socket = io('http://localhost:3000');
    socket.on('newBpmData', (newData) => {
      setBPM((prevBpm) => [newData, ...prevBpm]);

      setChartData((prevData) => {
        const newLabels = [new Date(newData.timestamp).toLocaleString(), ...prevData.labels];
        const newDatasetData = [newData.beatAvg, ...prevData.datasets[0].data];

        return {
          ...prevData,
          labels: newLabels.slice(0, 20), // Limit to 20 data points
          datasets: [
            {
              ...prevData.datasets[0],
              data: newDatasetData.slice(0, 20),
            },
          ],
        };
      });
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div>
      <Header />
      <div className="container mt-5">
        <br /><br />
        <h2 className="mb-4">BPM History</h2>
        <div className="col">
          <a href="/bpm/pulse/new" className="btn btn-success">Add New BPM</a>
        </div>
        <Line data={chartData} />
        <table className="table table-dark table-striped mt-4">
          <thead>
            <tr>
              <th>Name</th>
              <th>Age</th>
              <th>Average Beat</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {bpm.map(bpm => (
              <tr key={bpm._id}>
                <td>{bpm.name}</td>
                <td>{bpm.age}</td>
                <td>{bpm.beatAvg}</td>
                <td>{new Date(bpm.timestamp).toDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
};

export default BpmList;
