import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { Line } from 'react-chartjs-2';
import Header from './header';
import Footer from './footer';

const BpmList = () => {
  const [bpm, setBpm] = useState([]);
  const [latestBpm, setLatestBpm] = useState(null);
  

  useEffect(() => {
    const fetchBpm = async () => {
      try {
        const response = await axios.get('http://localhost:3000/bpm/getlist');
        setBpm(response.data.bpm);

        if (response.data.bpm.length > 0) {
          const latest = response.data.bpm[response.data.bpm.length-1];
          setLatestBpm(latest);

          updateChartData(latest);
        }
      } catch (error) {
        console.error('Error fetching Bpm List:', error);
      }
    };

    fetchBpm();

    const socket = io('http://localhost:3000');
    socket.on('newBpmData', (newData) => {
      setLatestBpm(newData);
      updateChartData(newData);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const updateChartData = (data) => {
    const { bpmReadings } = data;
    const labels = bpmReadings.map((reading) => new Date(reading.bpmtime).toLocaleString());
    const beats = bpmReadings.map((reading) => reading.beats);

    setChartData({
      labels,
      datasets: [
        {
          label: 'Beats',
          data: beats,
          borderColor: 'rgba(75,192,192,1)',
          backgroundColor: 'rgba(75,192,192,0.2)',
          fill: false,
        },
      ],
    });
  };

  return (
    <div>
      <Header />
      <div className="container mt-5">
        <br /><br />
        <h2 className="mb-4">BPM History</h2>
        <div className="col">
          
        </div>

        <Line data={chartData} />
        <table className="table table-dark table-striped mt-4" style={{ marginBottom: '10%' }}>
          <thead>
            <tr>
              <th>Average Beat</th>
              <th>Status</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {bpm.map((bpmData) => (
              <tr key={bpmData._id}>
                <td>{bpmData.beatAvg}</td>
                <td>{bpmData.beatStatus}</td>
                <td>{new Date(bpmData.timestamp).toDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
};

export default BpmList;
