import React, { useState } from 'react';
import axios from 'axios';
import Header from '../components/header'; 
import Footer from '../components/footer';

const NewOrderForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    items: [{ type: '', kilo: '' }],
    service: 'Washing',
    description: ''
  });

  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const newFormData = { ...formData };
    if (name === 'type' || name === 'kilo') {
      newFormData.items[index][name] = value;
    } else {
      newFormData[name] = value;
    }
    setFormData(newFormData);
  };

  const handleAddItem = () => {
    setFormData({
      ...formData,
      items: [...formData.items, { type: '', kilo: '' }]
    });
  };

  const handleRemoveItem = index => {
    const newItems = [...formData.items];
    newItems.splice(index, 1);
    setFormData({ ...formData, items: newItems });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const orderData = {
        customer: {
          firstName: formData.firstName,
          lastName: formData.lastName
        },
        items: formData.items,
        service: formData.service,
        description: formData.description
      };

      await axios.post('http://localhost:3000/order/postrder', orderData);
      alert('Order submitted successfully');
    } catch (error) {
      console.error('Error submitting order:', error);
      alert('Error submitting order');
    }
  };

  return (
    <div>
      <Header />
      <div className="container">
        <br />
        <h1 className="mb-4">New Order Form</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="firstName">First Name:</label>
            <input type="text" id="firstName" name="firstName" value={formData.firstName} onChange={handleInputChange} />
          </div>

          <div className="form-group">
            <label htmlFor="lastName">Last Name: </label>
            <input type="text" id="lastName" name="lastName" value={formData.lastName} onChange={handleInputChange} />
          </div>

          <div>
            {formData.items.map((item, index) => (
              <div key={index}>
                <div className="form-group">
                  <label htmlFor={`type${index}`}>Item Type:</label>
                  <select id={`type${index}`} name="type" value={item.type} onChange={e => handleInputChange(e, index)}>
                    <option value="regular load">Regular Load</option>
                    <option value="heavy load">Heavy Load</option>
                  </select>
                </div>
                <div className="form-group">
                  <label htmlFor={`kilo${index}`}>Kilos:</label>
                  <input type="number" id={`kilo${index}`} name="kilo" value={item.kilo} onChange={e => handleInputChange(e, index)} />
                </div>
                {index > 0 && <button type="button" onClick={() => handleRemoveItem(index)}>Remove</button>}
              </div>
            ))}
            <button type="button" onClick={handleAddItem}>Add Item</button>
          </div>

          <div className="form-group">
            <label htmlFor="service">Service:</label>
            <select id="service" name="service" value={formData.service} onChange={handleInputChange}>
              <option value="Washing">Washing</option>
              <option value="Drying">Drying</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="description">Description:</label>
            <textarea id="description" name="description" value={formData.description} onChange={handleInputChange}></textarea>
          </div>
          
          <button type="submit" className="btn btn-primary">Submit</button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default NewOrderForm;
