import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { Line } from 'react-chartjs-2';
import Header from '../components/header';
import Footer from '../components/footer';

const BpmList = () => {
  const [bpm, setBpm] = useState([]);
  const [latestBpm, setLatestBpm] = useState(null);
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [
      {
        label: 'Average Beat',
        data: [],
        borderColor: 'rgba(75,192,192,1)',
        backgroundColor: 'rgba(75,192,192,0.2)',
        fill: true,
      },
    ],
  });

  useEffect(() => {
    const fetchBpm = async () => {
      try {
        const response = await axios.get('http://localhost:3000/bpm/getlist');
        setBpm(response.data.bpm);

        if (response.data.bpm.length > 0) {
          const latest = response.data.bpm[0];
          setLatestBpm(latest);

          updateChartData(latest);
        }
      } catch (error) {
        console.error('Error fetching Bpm List:', error);
      }
    };

    fetchBpm();

    const socket = io('http://localhost:3000');
    socket.on('newBpmData', (newData) => {
      setLatestBpm(newData);
      updateChartData(newData);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const updateChartData = (data) => {
    setChartData({
      labels: [new Date(data.timestamp).toLocaleString()],
      datasets: [
        {
          label: 'Average Beat',
          data: [data.beatAvg],
          borderColor: 'rgba(75,192,192,1)',
          backgroundColor: 'rgba(75,192,192,0.2)',
          fill: true,
        },
      ],
    });
  };

  return (
    <div>
      <Header />
      <div className="container mt-5">
        <br /><br />
        <h2 className="mb-4">BPM History</h2>
        <div className="col">
          <a href="/bpm/pulse/new" className="btn btn-success">Add New BPM</a>
        </div>
        <Line data={chartData} />
        <table className="table table-dark table-striped mt-4" style={{ marginBottom: '10%' }}>
          <thead>
            <tr>
              <th>Average Beat</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {bpm.map((bpmData) => (
              <tr key={bpmData._id}>
                <td>{bpmData.beatAvg}</td>
                <td>{new Date(bpmData.timestamp).toDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
};

export default BpmList;
