import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header'; 
import Footer from '../components/footer';

const OrderList = () => {
  // State to store orders
  const [orders, setOrders] = useState([]);
  // Filtered orders based on status
  const [filteredOrders, setFilteredOrders] = useState([]);
  // State to manage confirmation modal
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [orderIdToDelete, setOrderIdToDelete] = useState(null);

  // Function to fetch orders from the server
  const fetchOrders = async () => {
    try {
      const response = await axios.get('http://localhost:3000/order/getorders');
      setOrders(response.data); // Assuming your API response contains an array of orders directly
      setFilteredOrders(response.data); // Initialize filtered orders with all orders
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  // Fetch orders on component mount
  useEffect(() => {
    fetchOrders();
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  // Function to handle status update for a specific order
  const handleStatusUpdate = async (orderId, newStatus) => {
    try {
      // Send PUT request to update status in the database
      await axios.put(`http://localhost:3000/order/updatestatus/${orderId}`, { status: newStatus });
      // After updating status, fetch orders again to reflect changes
      fetchOrders();
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  // Function to handle order deletion
  const handleOrderDelete = async (orderId) => {
    try {
      // Send DELETE request to delete the order
      await axios.delete(`http://localhost:3000/order/delete/${orderId}`);
      // After deletion, fetch orders again to reflect changes
      fetchOrders();
      // Close the confirmation modal
      setShowConfirmation(false);
    } catch (error) {
      console.error('Error deleting order:', error);
    }
  };

  // Function to open confirmation modal
  const openConfirmationModal = (orderId) => {
    setOrderIdToDelete(orderId);
    setShowConfirmation(true);
  };

  // Function to close confirmation modal
  const closeConfirmationModal = () => {
    setShowConfirmation(false);
  };

  // Function to filter orders by status
  const filterOrdersByStatus = (status) => {
    const filtered = orders.filter(order => order.status === status);
    setFilteredOrders(filtered);
  };

  return (
    <div>
      <Header />
      <div className="container">
        <br /><br />
        <h1>Order List</h1>
        <a href="/labaduh/order/new" className="btn btn-success">Create New Order</a>
        <div className="row mt-4">
          <div className="col">
            <div className="btn-group" role="group" aria-label="Status Filter">
              <button type="button" className="btn btn-secondary" onClick={() => filterOrdersByStatus('pending')}>Pending</button>
              <button type="button" className="btn btn-secondary" onClick={() => filterOrdersByStatus('ready for pick-up')}>Ready for Pick-up</button>
              <button type="button" className="btn btn-secondary" onClick={() => filterOrdersByStatus('picked-up')}>Picked-up</button>
            </div>
          </div>
        </div>
        <div className="row mt-4">
          {filteredOrders && filteredOrders.length > 0 ? (
            filteredOrders.map(order => (
              <div key={order._id} className="col-md-4 mb-4">
                <div className="card h-100"> {/* Added h-100 class to ensure fixed height */}
                  <div className="card-body d-flex flex-column"> {/* Added d-flex and flex-column classes for flex layout */}
                    <h5 className="card-title">Order Number: {order.orderNumber}</h5>
                    <p className="card-text">Date: {new Date(order.createdAt).toLocaleDateString()}</p>
                    <p className="card-text">Customer Name: {order.firstName} {order.lastName}</p>
                    <p className="card-text">Service: {order.service}</p>
                    <p className="card-text">Type: {order.type}</p>
                    <p className="card-text">Kilo: {order.kilo}</p>
                    <p className="card-text">Price: ${order.price}</p>

                    <div className="form-group mt-auto"> {/* Added mt-auto to push content to the top */}
                      <label htmlFor={`status-${order._id}`}>Status: </label>
                      <select id={`status-${order._id}`} className="form-control" value={order.status} onChange={(e) => handleStatusUpdate(order._id, e.target.value)}>
                        <option value="pending">Pending</option>
                        <option value="ready for pick-up">Ready for Pick-up</option>
                        <option value="picked-up">Picked-up</option>
                      </select>
                    </div>

                    {/* Delete button with confirmation modal */}
                    <button className="btn btn-danger mt-2" onClick={() => openConfirmationModal(order._id)}>Delete</button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </div>

      {/* Confirmation modal */}
      {showConfirmation && (
        <div className="modal" style={{ display: 'block', backgroundColor: 'rgba(0, 0, 0, 0.5)', position: 'fixed', top: 0, bottom: 0, left: 0, right: 0 }}>
          <div className="modal-dialog" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Confirm Deletion</h5>
              </div>
              <div className="modal-body">
                <p>Are you sure you want to delete this order?</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={closeConfirmationModal}>Cancel</button>
                <button type="button" className="btn btn-danger" onClick={() => handleOrderDelete(orderIdToDelete)}>Delete</button>
              </div>
            </div>
          </div>
        </div>
      )}

      <Footer/>
    </div>
  );
};

export default OrderList;
