import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header'; 
import Footer from '../components/footer';

const NewOrderForm = () => {
  const [formData, setFormData] = useState({
    customerOption: 'existing', // Default to selecting from existing customers
    firstName: '',
    lastName: '',
    items: '',
    itemType: 'regular load',
    kilos: '',
    description: ''
  });

  const [registeredCustomers, setRegisteredCustomers] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);

  useEffect(() => {
    fetchRegisteredCustomers();
  }, []);

  const fetchRegisteredCustomers = async () => {
    try {
      const response = await axios.get('/api/customer');
      setRegisteredCustomers(response.data.customers); // Set all registered customers
    } catch (error) {
      console.error('Error fetching registered customers:', error);
    }
  };

  const handleCustomerOptionChange = e => {
    const { value } = e.target;
    setFormData({ ...formData, customerOption: value });
    if (value === 'others') {
      setSelectedCustomer(null); // Clear selected customer when selecting 'Others'
    }
  };

  const handleRegisteredCustomerChange = e => {
    const customerId = e.target.value;
    const selectedCustomer = registeredCustomers.find(customer => customer._id === customerId);
    setSelectedCustomer(selectedCustomer);
    // Automatically fill first name and last name fields
    setFormData({
      ...formData,
      firstName: selectedCustomer.firstName,
      lastName: selectedCustomer.lastName
    });
  };

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      let customer;

      if (formData.customerOption === 'existing' && selectedCustomer) {
        customer = selectedCustomer;
      } else {
        const newCustomerData = {
          firstName: formData.firstName,
          lastName: formData.lastName
          // Add other customer data as needed
        };
        const response = await axios.post('/customer', newCustomerData);
        customer = response.data.customer;
      }

      const orderData = {
        customer: customer._id,
        items: [
          {
            type: formData.itemType,
            kilo: formData.kilos
          }
        ],
        description: formData.description
      };

      await axios.post('/order/new', orderData);
      alert('Order submitted successfully');
    } catch (error) {
      console.error('Error submitting order:', error);
      alert('Error submitting order');
    }
  };

  return (
    <div>
      <Header />
      <div className="container">
        <br />
        <h1 className="mb-4">New Order Form</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="customerOption">Select Customer</label>
            <select className="form-control" id="customerOption" name="customerOption" value={formData.customerOption} onChange={handleCustomerOptionChange}>
              <option value="existing">Registered Customer</option>
              <option value="others">Others</option>
            </select>
          </div>
          {formData.customerOption === 'existing' ? (
            <div className="form-group">
              <label htmlFor="registeredCustomer">Registered Customer</label>
              <select className="form-control" id="registeredCustomer" name="registeredCustomer" onChange={handleRegisteredCustomerChange}>
                <option value="">Select Customer</option>
                {registeredCustomers.map(customer => (
                  <option key={customer._id} value={customer._id}>{customer.firstName} {customer.lastName}</option>
                ))}
              </select>
            </div>
          ) : (
            <div>
              <div className="form-group">
                <label htmlFor="firstName">First Name</label>
                <input type="text" className="form-control" id="firstName" name="firstName" value={formData.firstName} onChange={handleInputChange} />
              </div>
              <div className="form-group">
                <label htmlFor="lastName">Last Name</label>
                <input type="text" className="form-control" id="lastName" name="lastName" value={formData.lastName} onChange={handleInputChange} />
              </div>
            </div>
          )}
          <div className="form-group">
            <label htmlFor="itemType">Item Type</label>
            <select className="form-control" id="itemType" name="itemType" value={formData.itemType} onChange={handleInputChange}>
              <option value="regular load">Regular Load</option>
              <option value="heavy load">Heavy Load</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="kilos">Kilos</label>
            <input type="number" className="form-control" id="kilos" name="kilos" value={formData.kilos} onChange={handleInputChange} />
          </div>
          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea className="form-control" id="description" name="description" value={formData.description} onChange={handleInputChange}></textarea>
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
        </form>
      </div>
      <Footer/>
    </div>
  );
};

export default NewOrderForm;
