import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header'; 
import Footer from '../components/footer';
import '../css/orderform.css';
import moment from 'moment-timezone';


const newBpm = () => {
    const [formData, setFormData] = useState({
        Name: '',
        Age:0
    });

  const { Name, Age } = formData;



  const handleSubmit = async (event) => {
    event.preventDefault();

    try {

      const createdAtPHTime = moment().tz('Asia/Manila').format();

      // Post the order data to the server
      const response = await axios.put("http://localhost:3000/bpm/newbpm", {
        Name,
        Age,
        createdAt: createdAtPHTime  // Add createdAt field with current date
      });

    } catch (err) {
      console.error(err);
    }
  };


  return (
    <div>
      <Header />
      <div className="no-container">
        <br />
        <h1 className="mb-4">New BPM</h1>
        <form onSubmit={handleSubmit}>
        
          <div className="fgg">
            <label htmlFor="firstName">Name:</label>
            <input type="text" className="form-control" id="Name" value={Name} onChange={(event) => setFormData({ ...formData, Name: event.target.value })} />
          </div>

          

          <div className="fgg">
            <label htmlFor="kilo">Kilos:</label>
            <input type="number" className="form-control" id="Age" name="Age" value={Age} onChange={(event) => setFormData({ ...formData, Age: event.target.value })} />
          </div>


          <button type="submit" className="no-btn">Submit</button>
        </form>
      </div>

      <Footer />
    </div>
  );
};

export default newBpm;
