import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { Line } from 'react-chartjs-2';
import Header from './header';
import Footer from './footer';

const BpmHistory = () => {
  const [bpm, setBpm] = useState([]);

  useEffect(() => {
    const fetchBpm = async () => {
      try {
        const response = await axios.get('http://localhost:3000/bpm/getlist');
        setBpm(response.data.bpm);

        if (response.data.bpm.length > 0) {
          const latest = response.data.bpm[response.data.bpm.length-1];
          setLatestBpm(latest);

          updateChartData(latest);
        }
      } catch (error) {
        console.error('Error fetching Bpm List:', error);
      }
    };

    fetchBpm();
  }, []);


  return (
    <div>
      <Header />
      <div className="container mt-5">
        <br /><br />
        <h2 className="mb-4">BPM History</h2>
        <div className="col">
          
        </div>

        <Line data={chartData} />
        <table className="table table-dark table-striped mt-4" style={{ marginBottom: '10%' }}>
          <thead>
            <tr>
              <th>Average Beat</th>
              <th>Status</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {bpm.map((bpmData) => (
              <tr key={bpmData._id}>
                <td>{bpmData.beatAvg}</td>
                <td>{bpmData.beatStatus}</td>
                <td>{new Date(bpmData.timestamp).toDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
};

export default BpmHistory;
