import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto'; // Import Chart.js library
import Header from '../components/header'; // Import Header component
import Footer from '../components/footer'; // Import Footer component

const Home = () => {
  const [orderStatusCounts, setOrderStatusCounts] = useState({});
  const [incomeData, setIncomeData] = useState([]);
  const [ordersPerDayData, setOrdersPerDayData] = useState([]);

  useEffect(() => {
    // Fetch data for order status counts
    axios.get('http://localhost:3000/order/statusCounts')
      .then(response => {
        setOrderStatusCounts(response.data);
      })
      .catch(error => {
        console.error("Error fetching order status counts:", error);
      });

    // Fetch data for income
    axios.get("http://localhost:3000/order/income")
      .then(response => {
        setIncomeData(response.data);
      })
      .catch(error => {
        console.error("Error fetching income data:", error);
      });

    // Fetch data for orders per day in a week
    axios.get("http://localhost:3000/order/orderPerDay")
      .then(response => {
        setOrdersPerDayData(response.data);
      })
      .catch(error => {
        console.error("Error fetching orders per day data:", error);
      });
  }, []);

useEffect(() => {
  // Draw charts here using orderStatusCounts, incomeData, and ordersPerDayData
  // Example: Draw line chart for income data
  if (incomeData.length > 0) {
    const incomeChartCtx = document.getElementById('incomeChart').getContext('2d');
    
    // Check if there's an existing chart instance and destroy it
    if (window.incomeChart !== undefined) {
      window.incomeChart.destroy();
    }

    window.incomeChart = new Chart(incomeChartCtx, {
      type: 'line',
      data: {
        labels: incomeData.map(data => `${data.year}-${data.month}`),
        datasets: [{
          label: 'Income',
          data: incomeData.map(data => data.totalAmount),
          borderColor: 'blue',
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    });
  }
}, [incomeData]);

  



    // Example: Draw bar chart for orders per day data
    useEffect(() => {
      // Draw charts here using orderStatusCounts, incomeData, and ordersPerDayData
      // Example: Draw line chart for income data
      if (incomeData.length > 0) {
        const incomeChartCtx = document.getElementById('incomeChart').getContext('2d');
        new Chart(incomeChartCtx, {
          type: 'line',
          data: {
            labels: incomeData.map(data => `${data.year}-${data.month}`),
            datasets: [{
              label: 'Income',
              data: incomeData.map(data => data.totalAmount),
              borderColor: 'blue',
              borderWidth: 1
            }]
          },
          options: {
            scales: {
              y: {
                beginAtZero: true
              }
            }
          }
        });
      }
    }, [incomeData, ordersPerDayData]); // Include ordersPerDayData in the dependency array
    

  return (
    <div>
      <Header /> {/* Include Header component */}
      <div>
        <div className="jumbotron text-center">
          <br/><br/>
          <h1>Welcome to Our Website</h1>
          <p>We provide top-notch laundry services</p>
          <a href="/labaduh/order/new" className="btn btn-primary btn-lg">Place an Order</a>
        </div>
    
        <div className="container">
          <br />
          <br />
          <br />
          <div className="row">
            <div className="col-md-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Pending Orders</h5>
                  <p className="card-text">{orderStatusCounts.pending || 0}</p>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Orders Ready for Pickup</h5>
                  <p className="card-text">{orderStatusCounts['ready for pick-up'] || 0}</p>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Picked-up Orders</h5>
                  <p className="card-text">{orderStatusCounts['picked-up'] || 0}</p>
                </div>
              </div>
            </div>
          </div>

          <div className="row">
            <div className="col-md-6">
              <div className="chart-container">
                <h2 className="text-center">Income</h2>
                <canvas id="incomeChart"></canvas>
              </div>
            </div>
            <div className="col-md-6">
              <div className="chart-container">
                <h2 className="text-center">Orders per Day</h2>
                <canvas id="ordersPerDayChart"></canvas>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer /> {/* Include Footer component */}
    </div>
  );
};

export default Home;
