import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { Line, Bar } from 'react-chartjs-2';
import Header from './header';
import Footer from './footer';
import '../css/bpmlist.css';

const BpmHistory = () => {
  const [bpm, setBpm] = useState([]);
  const [statusCounts, setStatusCounts] = useState({});

  useEffect(() => {
    const fetchBpm = async () => {
      try {
        const response = await axios.get('http://localhost:3000/bpm/getlist');
        setBpm(response.data.bpm);
        calculateStatusCounts(response.data.bpm);
      } catch (error) {
        console.error('Error fetching Bpm List:', error);
      }
    };

    fetchBpm();
  }, []);

  const calculateStatusCounts = (bpmData) => {
    const counts = bpmData.reduce((acc, curr) => {
      acc[curr.beatStatus] = (acc[curr.beatStatus] || 0) + 1;
      return acc;
    }, {});
    setStatusCounts(counts);
  };

   // Chart.js data for the bar chart
   const chartData = {
    labels: Object.keys(statusCounts),
    datasets: [
      {
        label: 'Status Counts',
        data: Object.values(statusCounts),
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 206, 86, 0.6)',
          'rgba(75, 192, 192, 0.6)',
          'rgba(153, 102, 255, 0.6)',
          'rgba(255, 159, 64, 0.6)',
        ],
      },
    ],
  };

  return (
    <div>
      <Header />
      <div className="container mt-5">
        <br /><br />
        <h2 className="mb-4">BPM History</h2>
        <div className="bac">
            <a href="/bpm/pulse" className="btn btn-success">Back</a>
        </div>
        <div className="col">
          {/* Bar chart */}
          <div className="chart-container">
            <Bar
              data={chartData}
              options={{
                responsive: true,
                scales: {
                  yAxes: [
                    {
                      ticks: {
                        beginAtZero: true,
                      },
                    },
                  ],
                },
              }}
            />
          </div>
        </div>

        <table className="table table-dark table-striped mt-4" style={{ marginBottom: '10%' }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Age</th>
              <th>Sex</th>
              <th>Average Beat</th>
              <th>Status</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {bpm.map((bpmData) => (
              <tr key={bpmData._id}>
                <td>{bpmData.name}</td>
                <td>{bpmData.age}</td>
                <td>{bpmData.sex}</td>

                <td>{bpmData.beatAvg}</td>
                <td>{bpmData.beatStatus}</td>
                <td>{new Date(bpmData.timestamp).toDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
};

export default BpmHistory;
