import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header'; 
import Footer from '../components/footer';

const EReceipt = ({ orderId }) => {
  const [orderDetails, setOrderDetails] = useState(null);

  useEffect(() => {
    const fetchOrderDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/order/getOrderDetails/${orderId}`);
        setOrderDetails(response.data);
      } catch (error) {
        console.error('Error fetching order details:', error);
      }
    };

    fetchOrderDetails();
  }, [orderId]);

  if (!orderDetails) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>Electronic Receipt</h2>
      <div>
        <p>Order Number: {orderDetails.orderNumber}</p>
        <p>Customer: {orderDetails.firstName} {orderDetails.lastName}</p>
        <p>Service: {orderDetails.service}</p>
        <p>Kilos: {orderDetails.kilo}</p>
        <p>Price: {orderDetails.price}</p>
        {/* Additional details as needed */}
      </div>
    </div>
  );
};

export default EReceipt;
