import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTshirt, faHome, faListAlt, faUsers, faInfoCircle, faEnvelope, faUserCircle, faUser, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';
import logo from '../images/5.png'; 
import '../css/header.css';

const Header = () => {
  const [activeItem, setActiveItem] = useState('home'); // Default active item is 'home'

  const handleItemClick = (itemName) => {
    setActiveItem(itemName);
  };

  return (
    <header className="fixed-top">
      <nav className="navbar navbar-expand-lg navbar-dark  justify-content-center custom-navbar">
        <div className="container">
          <a className="navbar-brand" href="#">
            <img src={logo} alt="Laundry App Logo" width="180" height="80" className="d-inline-block align-top" />
          </a>
          <span className="ml-3 text-white"></span>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
            <ul className="navbar-nav">
              <li className={`nav-item ${activeItem === 'home' ? 'active' : ''}`}>
                <a className="nav-link" href="/home" onClick={() => handleItemClick('home')}><FontAwesomeIcon icon={faHome} className="mr-1" />Home</a>
              </li>
              <li className={`nav-item ${activeItem === 'orders' ? 'active' : ''}`}>
                <a className="nav-link" href="/labaduh/order" onClick={() => handleItemClick('orders')}><FontAwesomeIcon icon={faListAlt} className="mr-1" />Orders</a>
              </li>
                
              <li className="nav-item dropdown">
                <a className="nav-link dropdown-toggle" href="#" id="navbarDropdownMenuLink" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                  <FontAwesomeIcon icon={faSignOutAlt}  className="mr-1" />Logout
                </a>
                <div className="dropdown-menu" aria-labelledby="navbarDropdownMenuLink">
                  <a className="dropdown-item" href="#"><FontAwesomeIcon icon={faUser} className="mr-1" />Profile</a>
                  <a className="dropdown-item" href="#"><FontAwesomeIcon icon={faSignOutAlt} className="mr-1" />Logout</a>
                </div>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default Header;
